(7438, 257, 'Welcome to you new +PlusPro Account', '<span>\r\n\r\nReally Simple HTML Email Template\r\n\r\n/* ------------------------------------- \r\n		GLOBAL \r\n------------------------------------- */\r\n* { \r\n	margin:0;\r\n	padding:0;\r\n	font-family: "Helvetica Neue", "Helvetica", Helvetica, Arial, sans-serif; \r\n	font-size: 100%;\r\n	line-height: 1.6;\r\n}\r\n\r\nimg { \r\n	max-width: 100%; \r\n}\r\n\r\nbody {\r\n	-webkit-font-smoothing:antialiased; \r\n	-webkit-text-size-adjust:none; \r\n	width: 100%!important; \r\n	height: 100%;\r\n}\r\n\r\n\r\n/* ------------------------------------- \r\n		ELEMENTS \r\n------------------------------------- */\r\na { \r\n	color: #348eda;\r\n}\r\n\r\n.btn-primary{\r\n	text-decoration:none;\r\n	color: #FFF;\r\n	background-color: #348eda;\r\n	border:solid #348eda;\r\n	border-width:10px 20px;\r\n	line-height:2;\r\n	font-weight:bold;\r\n	margin-right:10px;\r\n	text-align:center;\r\n	cursor:pointer;\r\n	display: inline-block;\r\n	border-radius: 25px;\r\n}\r\n\r\n.btn-secondary {\r\n	text-decoration:none;\r\n	color: #FFF;\r\n	background-color: #aaa;\r\n	border:solid #aaa;\r\n	border-width:10px 20px;\r\n	line-height:2;\r\n	font-weight:bold;\r\n	margin-right:10px;\r\n	text-align:center;\r\n	cursor:pointer;\r\n	display: inline-block;\r\n	border-radius: 25px;\r\n}\r\n\r\n.last { \r\n	margin-bottom: 0;\r\n}\r\n\r\n.first{\r\n	margin-top: 0;\r\n}\r\n\r\n.padding{\r\n	padding:10px 0;\r\n}\r\n\r\n\r\n/* ------------------------------------- \r\n		BODY \r\n------------------------------------- */\r\ntable.body-wrap { \r\n	width: 100%;\r\n	padding: 20px;\r\n}\r\n\r\ntable.body-wrap .container{\r\n	border: 1px solid #f0f0f0;\r\n}\r\n\r\n\r\n/* ------------------------------------- \r\n		FOOTER \r\n------------------------------------- */\r\ntable.footer-wrap { \r\n	width: 100%;	\r\n	clear:both!important;\r\n}\r\n\r\n.footer-wrap .container p {\r\n	font-size:12px;\r\n	color:#666;\r\n	\r\n}\r\n\r\ntable.footer-wrap a{\r\n	color: #999;\r\n}